*/ 
  return (
    <View style={styles.container}>
      <Text style={styles.title}>FoodApp</Text>
      <Text> Escolha sua refeição abaixo!</Text>
      <RestauranteMenu id={1} />
      <RestauranteMenu id={2} />

      <Text style={{fontSize:14}}> Total: R$ {(total).toFixed(2)} </Text>

      <ItemCardapio
        titulo="macarrão"
        imagem="../img/macarrao.png"
        descricao="macarronada de domingo"
        preco="10"
        amount={amountCardapio}
        handler={setItemCardapio} 
      />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor:"grey",
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
  },
  separator: {
    marginVertical: 30,
    height: 1,
  },
  cardapioText:{
    fontsize:12,
    alignItems: "flex-start",
    justifyContent:'center',
  },
  cardapioTitle:{
    fontSize: 16,
    fontWeight: 'bold',
    alignItems: "flex-start",
    justifyContent:'baseline',
  },
});
