import React, {useState} from 'react';
import { StyleSheet, Text, View, Image } from 'react-native';
import ToggleableMenu from './components/ToggleableMenu';

export default function App() {
  const [count,setCount] = useState(0);
  return (
    <View style={styles.container}>
      <Text style={styles.title}>FoodApp</Text>
      <Text> Escolha sua refeição abaixo!</Text>
      <ToggleableMenu id={1} />
      <ToggleableMenu id={2} />
      <Text>Click: {count}</Text>
      <button onClick={() => setCount(count+1)}>
      Click me!
      </button>
      <View style={{flexDirection:"row", textAlignVertical:"center"}}>
        <View style={{flex:1}}>
          <Image 
            source={require('./img/Feijoada.png')} 
            style ={{width:100, height:100}}
          />
        </View>
        <View style={{flex:1,textAlignVertical:"center"}}>
          <Text style={styles.cardapioText}> Feijoada completa</Text>
        </View>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
  },
  separator: {
    marginVertical: 30,
    height: 1,
    width: '80%',
  },
  cardapioText:{
    flex:1,
    textAlignVertical: "center",
  },
});
