import React from "react";
import { StyleSheet, View, Text, Image } from "react-native";

const ItemCardapio = ({titulo, imagem, descricao,preco,increment,decrement}) => {
  return (
    <View>
      <View style={styles.container}>
        <View style={styles.imageContainer}>
          <Image style ={styles.image} source={require('./img/Feijoada.png')} />
        </View>
        <View style={{width:'60%',padding:15, alignItems:'baseline',justifyContent:'center'}}>
          <Text style={styles.cardapioTitle}>Feijoada completa</Text>
          <Text style={styles.cardapioText}>Deliciosa feijoada contendo arroz, feijão, linguiça couve e farofa.</Text>
        </View>
        <View style={{flex:1,width:'10%', justifyContent:'center'}}>
          <Text>R$ {(feijoada * 20).toFixed(2)}</Text>
          <View style={{flexDirection:'row'}}>
            <button onClick={() => 
              feijoada === 0 ? null : setFeijoada(feijoada-1)}>
              -
            </button>
            <TextInput 
              style={{padding:5, borderWidth:1, marginHorizontal:1, width:20}}
              onChangeText={setFeijoada}
              value={feijoada}
              placeholder={feijoada}
              keyboardType={'numeric'}
            />
            <button onClick={() => setFeijoada(feijoada+1)}>+</button>
          </View>
        </View>
      </View>

      <Text>{titulo}:{descricao}:{preco}</Text>
    </View>
  );
}

export default ItemCardapio;

const styles = StyleSheet.create({
  container: {
    flex:1,
    flexDirection:"row",
    height:100,
    margin:10,
    padding:5,
    backgroundColor:'lightblue',
  },
  imageContainer: {
    flex:1,
    justifyContent:'center',
    width:'20%',
    padding:5,
    margin:1,
  },
  image:{
    width:100,
    height:100,
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
  },
  cardapioText:{
    fontsize:12,
    alignItems: "flex-start",
    justifyContent:'center',
  },
  cardapioTitle:{
    fontSize: 16,
    fontWeight: 'bold',
    alignItems: "flex-start",
    justifyContent:'baseline',
  },
});
