import React, {useState,useEffect} from 'react';
import { StyleSheet, Text, TextInput, View, Image } from 'react-native';
import ToggleableMenu from './components/ToggleableMenu';


export default function App() {
  const [feijoada,setFeijoada] = useState(0);
  const [total,setTotal] = useState(0);


  useEffect(() => {
    setTotal(feijoada*20)
  },[feijoada]);

  return (
    <View style={styles.container}>
      <Text style={styles.title}>FoodApp</Text>
      <Text> Escolha sua refeição abaixo!</Text>
      <ToggleableMenu id={1} />
      <ToggleableMenu id={2} />

      <View style={{flexDirection:"row", height:100}}>
        <View style={{flex:1,justifyContent:'center', width:120}}>
          <Image 
            source={require('./img/Feijoada.png')} 
            style ={{width:100, height:100}}
          />
        </View>
        <View style={{flex:1,alignItems:'center',justifyContent:'center'}}>
          <Text style={styles.cardapioText}> Feijoada completa</Text>
        </View>
        <View style={{flex:1}}>
          <Text>R$ {(feijoada * 20).toFixed(2)}</Text>
          <View style={{flexDirection:'row'}}>
            <button onClick={() => 
              feijoada === 0 ? null : setFeijoada(feijoada-1)}>
              -
            </button>
            <TextInput 
              style={{padding:5, borderWidth:1, marginVertical:1}}
              onChangeText={setFeijoada}
              value={feijoada}
              placeholder={feijoada}
              keyboardType={'numeric'}
            />
            <button onClick={() => setFeijoada(feijoada+1)}>+</button>
          </View>
        </View>
      </View>

      <Text style={{fontSize:14}}> Total: R$ {(total).toFixed(2)} </Text>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
  },
  separator: {
    marginVertical: 30,
    height: 1,
    width: '80%',
  },
  cardapioText:{
    alignItems: "center",
    justifyContent:'center'
  },
});
