import React, {useState} from "react";
import { StyleSheet, View, Text, TouchableOpacity } from "react-native";

export default function ToggleableMenu(props) {
  const [toggle,setToggle] = useState(false);
  const [active,setActive] = useState(true);
  return (
  <View style={styles.container}>
    <TouchableOpacity
      style={styles.button}
      disabled={!active}
      onPress={()=> {
        setToggle(!toggle);
        setActive(!active)
      }}
    >
    {
      toggle==false?
      <Text style={styles.title}>
        {props.nomeRest}
      </Text>
      :
      <>
        <Text>O cardapio fica aqui</Text>
        <button
          onClick={() => {
            props.handler((props.total)+1);
          }
        }>
           + 
        </button>
        <Text>Valor:{props.total}</Text>
      </>
    }
    </TouchableOpacity>
  </View>
  );
}

const styles = StyleSheet.create({
  container: {
    backgroundColor: 'white',
    borderColor: '#D6D7DA',
    borderWidth: 2,
    borderRadius: 10,
    padding: 15,
    marginBottom: 0,
    alignItems:"center",
    justifyContent:"center",
  },
  altContainer: {
    backgroundColor: 'gray',
    borderColor: '#D6D7DA',
    borderWidth: 2,
    borderRadius: 10,
    padding: 15,
    margin: 15,
    marginBottom: 0,
},
  buttonPadding: {
    paddingHorizontal: 15,
  },
  button: {
    alignItems:"center",
    justifyContent:"center",
  },
  title: {
    fontSize:14,
    fontWeight:'bold',
  },
});
