import React, {useState} from 'react';
import { StyleSheet, View, Text} from 'react-native';

import ToggleableMenu from './ToggleableMenu';

export default function RestauranteMenu(props) {
  let escolhaRestaurante = ''
  if (props.id === 1) {
    escolhaRestaurante = 'McDonalds'
  } else if (props.id === 2) {
    escolhaRestaurante = 'Pizzaria do Bairro';
  } else {
    /* Deixando aqui uma opção default*/
    escolhaRestaurante = 'McDonalds'
  }

  const [toggle,setToggle] = useState(false);

  return ( 
    <View style={styles.menuContainer}>
      <ToggleableMenu nomeRest={escolhaRestaurante} />
    </View>
  );
}

const styles = StyleSheet.create({
  menuContainer: {
      backgroundColor: 'white',
      borderColor: '#D6D7DA',
      borderWidth: 2,
      borderRadius: 10,
      padding: 15,
      margin: 15,
      marginBottom: 0,
  },
  altMenuContainer: {
    backgroundColor: 'gray',
    borderColor: '#D6D7DA',
    borderWidth: 2,
    borderRadius: 10,
    padding: 15,
    margin: 15,
    marginBottom: 0,
},
  textContainer: {
      borderColor: '#D6D6DA',
      borderRadius: 2,
      borderWidth: 1,
      marginBottom: 5,
  },
  text: {
      height: 30,
      padding: 5,
      fontSize: 12,
  },
  button: {
    marginTop: 10,
    minWidth: 100,
    borderWidth: 2,
    borderRadius: 3,
  },
});
