import React from 'react';

import { StyleSheet, View, Text, Image, TextInput } from "react-native";

const ItemCardapio = ({titulo, imagem, descricao, preco, nroItems , setNroItems}) => {
  const pathImg = imagem.toString()
  return (
    <View>
      <View style={styles.container}>
        <View style={styles.imageContainer}>
          <Image style ={styles.image} source={require(pathImg)} />
        </View>
        <View style={styles.descriptionContainer}>
          <Text style={styles.cardapioTitle}>{titulo}</Text>
          <Text style={styles.cardapioText}>{descricao}</Text>
        </View>
        <View style={styles.quantityContainer}>
          <Text>R$ {(nroItems * 20).toFixed(2)}</Text>
          <View style={{flexDirection:'row'}}>
            <button onClick={setNroItems(nroItems -1)}> - </button>
            <TextInput 
              style={styles.textInput}
              onChangeText={setNroItems}
              value={nroItems}
              placeholder={nroItems}
              keyboardType={'numeric'}
            />
            <button onClick={setNroItems(nroItems +1)}> + </button>
          </View>
        </View>
      </View>

      <Text>{titulo}:{descricao}:{preco}</Text>
    </View>
  );
}

export default ItemCardapio;

const styles = StyleSheet.create({
  container: {
    flex:1,
    flexDirection:"row",
    height:100,
    margin:10,
    padding:5,
    backgroundColor:'lightblue',
  },
  imageContainer: {
    flex:1,
    justifyContent:'center',
    width:'20%',
    padding:5,
    margin:1,
  },
  image:{
    width:100,
    height:100,
  },
  descriptionContainer:{
    width:'60%',
    padding:15,
    alignItems:'baseline',
    justifyContent:'center',
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
  },
  cardapioText:{
    fontsize:12,
    alignItems: "flex-start",
    justifyContent:'center',
  },
  cardapioTitle:{
    fontSize: 16,
    fontWeight: 'bold',
    alignItems: "flex-start",
    justifyContent:'baseline',
  },
  quantityContainer:{
    flex:1,
    width:'10%',
    justifyContent:'center',
  },
  textInput: {
    padding:5,
    borderWidth:1,
    marginHorizontal:1,
    width:20,
  },
});
