import React, {useState} from "react";
import { StyleSheet, View } from "react-native";


export default function ToggleableMenu(props) {
  const [toggle,setToggle] = useState(false);

  return (
    <View style={styles.Container}>
    <TouchableOpacity
      style={[styles.button, { borderColor: 'red' }]}
      onPress={()=>setToggle(!toggle)}
    >
    {
      toggle==false?
      <Text>
        Aqui ficaria o menu do restaurante {props.nomeRest}.
      </Text>
      :
      <Text>O cardapio fica aqui</Text>
    }
    </TouchableOpacity>
  </View>
  );
}

const styles = StyleSheet.create({
  container: {
    backgroundColor: 'white',
    borderColor: '#D6D7DA',
    borderWidth: 2,
    borderRadius: 10,
    padding: 15,
    margin: 15,
    marginBottom: 0,
  },
  altContainer: {
    backgroundColor: 'gray',
    borderColor: '#D6D7DA',
    borderWidth: 2,
    borderRadius: 10,
    padding: 15,
    margin: 15,
    marginBottom: 0,
},
  buttonPadding: {
    paddingHorizontal: 15,
  },
});
