import React from 'react';

import { StyleSheet, View, Text } from 'react-native';

export default function RestauranteMenu(id) {
  let escolhaRestaurante = ''
  if (id === 1) {
    escolhaRestaurante = 'McDonalds'
  } else if (id === 2) {
    escolhaRestaurante = 'Pizzaria do Bairro';
  } else {
    /* Deixando aqui uma opção default*/
    escolhaRestaurante = 'McDonalds'
  }

  return ( 
    <View style={styles.menuContainer}>
      <TouchableOpacity
        style={[styles.button, { borderColor: color }]}
        onPress={onPress}
      >
        <Text>
          Aqui ficaria o menu do restaurante {escolhaRestaurante}.
        </Text>
      </TouchableOpacity>
    </View>
  );
}

const styles = StyleSheet.create({
  menuContainer: {
      backgroundColor: 'white',
      borderColor: '#D6D7DA',
      borderWidth: 2,
      borderRadius: 10,
      padding: 15,
      margin: 15,
      marginBottom: 0,
  },
  textContainer: {
      borderColor: '#D6D6DA',
      borderRadius: 2,
      borderWidth: 1,
      marginBottom: 5,
  },
  text: {
      height: 30,
      padding: 5,
      fontSize: 12,
  },
  button: {
    marginTop: 10,
    minWidth: 100,
    borderWidth: 2,
    borderRadius: 3,
  },
});
