import React from 'react';

import { StyleSheet, View, Text, Image, TextInput } from "react-native";

const ItemCardapio = (params) => {
  const pathImg = params.imagem

  function handleChange(event) {
    params.setItemCardapio(event.target.amountCardapio)
  }
  return (
    <View>
      <View style={styles.container}>
        <View style={styles.imageContainer}>
          <Image style ={styles.image} source={require('../img/Feijoada.png')} />
        </View>
        <View style={styles.descriptionContainer}>
          <Text style={styles.cardapioTitle}>{params.titulo}</Text>
          <Text style={styles.cardapioText}>{params.descricao}</Text>
        </View>
        <View style={styles.quantityContainer}>
          <Text>R$ {(params.amountCardapio * 20).toFixed(2)}</Text>
          <View style={{flexDirection:'row'}}>
            <button onClick={params.setItemCardapio(params.amountCardapio -1)}> - </button>
            
            <button onClick={params.setItemCardapio(params.amountCardapio +1)}> + </button>
          </View>
        </View>
      </View>

    </View>
  );
}

export default ItemCardapio;

const styles = StyleSheet.create({
  container: {
    flex:1,
    flexDirection:"row",
    height:100,
    margin:10,
    padding:5,
    backgroundColor:'lightblue',
  },
  imageContainer: {
    flex:1,
    justifyContent:'center',
    width:'20%',
    padding:5,
    margin:1,
  },
  image:{
    width:100,
    height:100,
  },
  descriptionContainer:{
    width:'60%',
    padding:15,
    alignItems:'baseline',
    justifyContent:'center',
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
  },
  cardapioText:{
    fontsize:12,
    alignItems: "flex-start",
    justifyContent:'center',
  },
  cardapioTitle:{
    fontSize: 16,
    fontWeight: 'bold',
    alignItems: "flex-start",
    justifyContent:'baseline',
  },
  quantityContainer:{
    flex:1,
    width:'10%',
    justifyContent:'center',
  },
  textInput: {
    padding:5,
    borderWidth:1,
    marginHorizontal:1,
    width:20,
  },
});
