import React, {useState,useEffect} from 'react';
import { StyleSheet, Text, View, Image } from 'react-native';
import ToggleableMenu from './components/ToggleableMenu';


export default function App() {
  const [feijoada,setFeijoada] = useState(0);
  let total = 0;

  useEffect(() => {
    total = feijoada
  },[total]);

  return (
    <View style={styles.container}>
      <Text style={styles.title}>FoodApp</Text>
      <Text> Escolha sua refeição abaixo!</Text>
      <ToggleableMenu id={1} />
      <ToggleableMenu id={2} />

      <View style={{flexDirection:"row", height:100}}>
        <View style={{flex:1,justifyContent:'center', width:120}}>
          <Image 
            source={require('./img/Feijoada.png')} 
            style ={{width:100, height:100}}
          />
        </View>
        <View style={{flex:1,alignItems:'center',justifyContent:'center'}}>
          <Text style={styles.cardapioText}> Feijoada completa</Text>
        </View>
        <View style={{flex:1}}>
          <Text>R$ {(feijoada * 20).toFixed(2)}</Text>
          <View style={{flexDirection:'row'}}>
            <button onClick={() => 
              feijoada === 0 ? null : setFeijoada(feijoada-1)}>
              -
            </button>
            <Text style={{width:20, justifyContent: 'center'}}>&nbsp;&nbsp;{feijoada}</Text>
            <button onClick={() => setFeijoada(feijoada+1)}>+</button>
          </View>
        </View>
      </View>

      <Text style={{fontSize:14}}> Total: {total} </Text>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
  },
  separator: {
    marginVertical: 30,
    height: 1,
    width: '80%',
  },
  cardapioText:{
    alignItems: "center",
    justifyContent:'center'
  },
});
