import React, {useState,useEffect} from 'react';
import { StyleSheet, Text, TextInput, View, Image } from 'react-native';
import ToggleableMenu from './components/ToggleableMenu';


export default function App() {
  const [feijoada,setFeijoada] = useState(0);
  const [total,setTotal] = useState(0);


  useEffect(() => {
    setTotal(feijoada*20)
  },[feijoada]);

  return (
    <View style={styles.container}>
      <Text style={styles.title}>FoodApp</Text>
      <Text> Escolha sua refeição abaixo!</Text>
      <ToggleableMenu id={1} />
      <ToggleableMenu id={2} />

      <View style={{flexDirection:"row", width:'70%', height:100, margin:10,padding:5, backgroundColor:'lightblue'}}>
        <View style={{flex:1,justifyContent:'center', width:'30%',padding:5, margin:1}}>
          <Image 
            source={require('./img/Feijoada.png')} 
            style ={{width:100, height:100}}
          />
        </View>
        <View style={{width:'60%',padding:15, alignItems:'baseline',justifyContent:'center'}}>
          <Text style={styles.cardapioTitle}>Feijoada completa</Text>
          <Text style={styles.cardapioText}>Deliciosa feijoada contendo arroz, feijão, linguiça couve e farofa.</Text>
        </View>
        <View style={{flex:1,width:'10%', justifyContent:'center'}}>
          <Text>R$ {(feijoada * 20).toFixed(2)}</Text>
          <View style={{flexDirection:'row'}}>
            <button onClick={() => 
              feijoada === 0 ? null : setFeijoada(feijoada-1)}>
              -
            </button>
            <TextInput 
              style={{padding:5, borderWidth:1, marginHorizontal:1, width:20}}
              onChangeText={setFeijoada}
              value={feijoada}
              placeholder={feijoada}
              keyboardType={'numeric'}
            />
            <button onClick={() => setFeijoada(feijoada+1)}>+</button>
          </View>
        </View>
      </View>

      <Text style={{fontSize:14}}> Total: R$ {(total).toFixed(2)} </Text>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
  },
  separator: {
    marginVertical: 30,
    height: 1,
    width: '80%',
  },
  cardapioText:{
    fontsize:12,
    alignItems: "flex-start",
    justifyContent:'center',
  },
  cardapioTitle:{
    fontSize: 16,
    fontWeight: 'bold',
    alignItems: "flex-start",
    justifyContent:'baseline',
  },
});
